    'careers.departments.finance.roles.receivable.title': 'Accounts Receivable Specialist',
    'careers.departments.finance.roles.receivable.desc': 'Manage customer payments, track receivables, and ensure timely collection of outstanding amounts.',
    'careers.departments.finance.roles.planning.title': 'Financial Planning Manager',
    'careers.departments.finance.roles.planning.desc': 'Develop financial strategies, create forecasts, and provide strategic financial planning support.',
