    // Services Page
    'services.hero.portfolio': 'Portföyümüzü keşfedin',
    'services.hero.weBuild': 'Biz inşa ediyoruz',
    'services.hero.title1': 'çarpıcı',
    'services.hero.title2': 'duyarlı',
    'services.hero.title3': 'yüksek dönüşümlü',
